import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useEffect, useState } from 'react'
import Characters from '../Characters';

const Fellowship = () => {
  const [quotes, setQuotes] = useState([]);
  const [characterQuote, setCharacterQuote] = useState('');
  const [characterName, setCharacterName] = useState('');
  const [modal, setModal] = useState(false);


  useEffect(() => {
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer 36H_uSF2UL6WB97Foiob'
    }

    const getQuotes = async () => {
      let data = await fetch('https://the-one-api.dev/v2/movie/5cd95395de30eff6ebccde5c/quote?character=5cd99d4bde30eff6ebccfc15', {
        headers: headers
      });
      let quotes = await data.json();
      let quoteData = quotes.docs;
      setQuotes(quoteData)
      // console.log(quoteData);
    }

    getQuotes();
  }, []);




  const handleClick = (character) => {
    const clickedCharacterQuotes = quotes.find(quote => quote.character === character.id)

    if (clickedCharacterQuotes) { // Set the quotes for the selected character
      setCharacterQuote(clickedCharacterQuotes.dialog) // set dialog/quote to state
      setCharacterName(character.name) // set name to state
      setModal(true)
      console.log(characterQuote)
     // const characterQuotePara = document.getElementById('characterQuotePara') // get paragraph element from character component
      //characterQuotePara.innerHTML = (characterQuote) // add character quote to paragraph
      const quoted = document.createElement("p");
      const characterQuotePara = document.getElementById('characterQuotePara').element.classList.remove(visually-hidden)

    } else {
      setCharacterQuote(character.name + ' had no lines in this film...');
    }
    // console.log(character.name + ': ' + characterQuote)
  }


  return (
    <div>
      <h1 className="display-1 text-center py-5">Click on a Character</h1>
      <Characters onClick={handleClick} characterQuote={characterQuote} />
      
      {modal && (
        <div className="modal fade show" tabIndex="-1" role="dialog">
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title fs-5">{characterName}</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" onClick={() => setModal(false)}></button>
              </div>
              <div className="modal-body">{characterQuote}</div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" onClick={() => setModal(false)}>Close</button>
              </div>
            </div>
          </div>
        </div>
        
      )}

<div id='characterQuotePara' class="position-absolute top-50 start-50 translate-middle p-10 visually-hidden">
<p className='fs-1 fw-bold text-warning'>"{characterQuote}"</p>
</div>

    </div>

  )
}

export default Fellowship;


