import {Link} from "react-router-dom";
import { useEffect, useState } from 'react'
import 'bootstrap/dist/css/bootstrap.css';

const TFOTRPage = () => {

  const [films, setFilms] = useState([])
  const [quotes, setQuotes] = useState([])
  const [filmId, setFilmId] = useState('')
  // const [filmName, setFilmName] = useState('')
    
    useEffect(() => {

        const headers = {
            'Accept': 'application/json',
            'Authorization': 'Bearer 36H_uSF2UL6WB97Foiob'
          }

        const getQuotes = async () => {
          let data = await fetch('https://the-one-api.dev/v2/movie/5cd95395de30eff6ebccde5c/quote', {
            headers: headers
          })
          let quotes = await data.json()
          console.log(quotes)
          let quoteData = quotes.docs 
          setQuotes(quoteData)
          // - returns array of all films
          console.log(quoteData)
      }
      
      getQuotes()
        
    }, [])
   
    const handleClick = (filmId) => {
      setFilmId(filmId)
      console.log('Film ID: ', filmId)
   }

    return (
      <>
      <h1>Lord of The Rings Quote Generator</h1>
      <h2>Choose a Film</h2>
        <div>
          <p>The Fellowship of the Ring</p>
          <p>The Two Towers</p>
          <p>The Return of the King</p>
         </div>
      </>
    )
}

export default TFOTRPage;
