    image: frodo,
  },

  {
    characterID: '5cd99d4bde30eff6ebccfd81',
    name: 'Legolas',
    image: frodo,
  }
  ]

  const DisplayCharacters = ({character}) => {
    return (
    <div className="row d-flex justify-content-center">
      <div className="col-sm-3">
          <div className="card border-0 mx-auto" style={{width: '18rem'}}>
            <img src={character.image} className="card-img-top" alt={`Image of ${character.name}`} />
            <div className="card-body">
              <p className="card-text">{character.name}</p>
             </div>
          </div>
      </div>
    </div>
    )
  }
 

  return (
<div>
  {characters.map((character) => <DisplayCharacters character={character} key={character.characterID} />)}
</div>
  )
  }


export default Characters;


