import { useEffect, useState } from 'react'
import 'bootstrap/dist/css/bootstrap.css';
import Characters from '../Characters';

const Fellowship = () => {
  const [quotes, setQuotes] = useState([]);
  const [characterQuote, setCharacterQuote] = useState([]);

  const [character, setCharacter] = useState([]);


  useEffect(() => {
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer 36H_uSF2UL6WB97Foiob'
    }

    const getQuotes = async () => {
      let data = await fetch('https://the-one-api.dev/v2/movie/5cd95395de30eff6ebccde5c/quote?limit=100', {
        headers: headers
      });
      let quotes = await data.json();
      let quoteData = quotes.docs;
      setQuotes(quoteData)
      // console.log(quoteData);
    }

    getQuotes();
  }, []);


  const handleClick = (character) => {
    const clickedCharacterQuotes = quotes.find(quote => quote.character === character.id);

    // Set the quotes for the selected character
    if (clickedCharacterQuotes) {
      setCharacterQuote(clickedCharacterQuotes.dialog);
    } else {
      // Handle the case where no matching quotes were found for the character
      setCharacterQuote('No quotes found for this character.');
    }




      const para = document.createElement("p")
      para.innerHTML = {characterQuote}
      document.getElementById('cardBody').appendChild(para);




    // setCharacter(character.id)
    console.log('you clicked ' + character.name)
    console.log(characterQuote)
  }

  return (
    <div>
      <h1 className="display-1 text-center py-5">Click on a Character</h1>
      <Characters onClick={handleClick} />
    </div>

  )
}

export default Fellowship;
