import { Link } from "react-router-dom";
import { useEffect, useState } from 'react'
import 'bootstrap/dist/css/bootstrap.css';
import './styles.css'
import Nav from "../Nav";
// import background from '../images/background.jpg'


const LandingPage = () => {

  const [films, setFilms] = useState([])
  const [filmId, setFilmId] = useState('')

  useEffect(() => {

    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer 36H_uSF2UL6WB97Foiob'
    }

    const getFilms = async () => {
      let data = await fetch('https://the-one-api.dev/v2/movie?_id!=5cd95395de30eff6ebccde56,5cd95395de30eff6ebccde57', {
        headers: headers
      })
      let films = await data.json()
      let filmData = films.docs
      setFilms(filmData)
      // - returns array of all films
      // console.log(filmData)
    }

    getFilms()

  }, [])

  return (
    <>
      <h1 className="display-1 text-center py-5">The Lord of the Rings Quote Generator</h1>
      <h2 className="display-2 text-center py-2">Choose a Film</h2>
      <div>
        <Link to={"/Fellowship"}><h3 className="text-center pt-5 pb-2 text-decoration-none">The Fellowship of the Ring</h3></Link>
        <h3 className="text-center py-2">The Two Towers</h3>
        <h3 className="text-center py-2 link-underline-opacity-0"> The Return of the King</h3>
      </div>
    </>
  )
}

export default LandingPage;
