import {Link} from "react-router-dom";
import { useEffect, useState } from 'react'

const GetAllFilms = () => {

  const [films, setFilms] = useState([])
  const [filmId, setFilmId] = useState()
    
    useEffect(() => {

        const headers = {
            'Accept': 'application/json',
            'Authorization': 'Bearer 36H_uSF2UL6WB97Foiob'
          }

        const getFilms = async () => {
            let data = await fetch('https://the-one-api.dev/v2/movie', {
              headers: headers
            })
            let films = await data.json()
            let filmData = films.docs 
            setFilms(filmData)
            // console.log(filmData)
        }
        
        getFilms()
        
    }, [])

    const handleClick = (filmData) => {
      console.log(filmData)
      let filmId = filmData.name
      setFilmId(filmId)
      console.log(filmId)
  }


    return (
      <>
      <h1>Choose a Film</h1>
        <div onClick={handleClick}>
          {films.map((film) => (<p key={film._id}> {film.name}</p>))}
         </div>
      </>
    )
}

export default GetAllFilms;
