import { Link } from "react-router-dom";
import { useEffect, useState } from 'react'
import 'bootstrap/dist/css/bootstrap.css';
import Characters from '../Characters';

const Fellowship = () => {
  const [quotes, setQuotes] = useState([]);
  const [characterQuote, setCharacterQuote] = useState([]);

  const [selectedCharacter, setSelectedCharacter] = useState(null);
    
  useEffect(() => {
    const headers = {
      'Accept': 'application/json',
      'Authorization': 'Bearer 36H_uSF2UL6WB97Foiob'
    }

    const getQuotes = async () => {
      let data = await fetch('https://the-one-api.dev/v2/movie/5cd95395de30eff6ebccde5c/quote?limit=100', {
        headers: headers
      });
      let quotes = await data.json();
      let quoteData = quotes.docs;
      setQuotes(quoteData)
      console.log(quoteData);
    }
      
    getQuotes();
  }, []);


  const handleClick = (character) => {
    setCharacter(character.id)
    console.log(selectedCharacter)
    console.log('you clicked ' + character.name)
 
  }

  useEffect(() => {
    if (selectedCharacter) {
      // Filter quotes for the selected character
      const selectedCharacterQuotes = quotes.filter(quote => quote.character === selectedCharacter.id);

      // Generate a random index to display a random quote
      const randomIndex = Math.floor(Math.random() * selectedCharacterQuotes.length);
      const randomQuote = selectedCharacterQuotes[randomIndex];

      setCharacterQuote(randomQuote.dialog);
    }
  }, [selectedCharacter, quotes]);


  return (
    
    <div>
        <h1 className="display-1 text-center py-5">Click on a Character</h1>
      <Characters onClick={handleClick}/>
    </div>
    
  )
}

export default Fellowship;


